eedComma _ true].			a add: self readAny]! !!Json methodsFor: 'private'!readDictionary	| m k v needComma |	m _ Dictionary new.	needComma _ false.	[true]		whileTrue: 			[self skipWhitespace.			self peek == $}				ifTrue: 					[self next.					^ m].			needComma				ifTrue: 					[self peek == $, ifFalse: [JsonSyntaxError signal: 'Missing comma'].					self next.					self skipWhitespace]				ifFalse: [needComma _ true].			self next == $" ifFalse: [JsonSyntaxError signal: 'Key in dictionary must be string'].			k _ self readString.			self skipWhitespace.			self peek == $: ifFalse: [JsonSyntaxError signal: 'Missing colon'].			self next.			v _ self readAny.			m at: k put: v]! !!Json methodsFor: 'private' stamp: 'ms 6/27/2011 19:08'!readNumber	| acc c |	acc _ WriteStream on: ''.	[true]		whileTrue: 			[c _ self peek.			(c isNil not and: [Json numbersMayContain: c])				ifFalse: [^ acc contents asNumber].						"on: JsonSyntaxError do: [JsonSyntaxError signal: 'Invalid number']]."			acc nextPut: c.			self next]! !!Json methodsFor: 'private'!readString	| s c |	s _ WriteStream on: ''.	[true]		whileTrue: 			[c _ self next.			c == $\				ifTrue: [s nextPut: self interpretStringEscape]				ifFalse: 					[c == $" ifTrue: [^ s contents].					s nextPut: c]]! !!Json methodsFor: 'private' stamp: 'tonyg 8/16/2005 20:32'!skipComment	self peek == $/ ifTrue: [		self next.		self peek == $/			ifTrue: [self skipToEndOfLine]			ifFalse: [self peek == $*						ifTrue: [self next. self skipCommentBody]						ifFalse: [JsonSyntaxError signal: 'Invalid comment syntax']]]! !!Json methodsFor: 'private' stamp: 'tonyg 8/17/2005 00:19'!skipCommentBody	[		[self next == $*] whileFalse.		self peek == $/	] whileFalse.	self next. "skip that last slash"	self skipWhitespace.! !!Json methodsFor: 'private' stamp: 'tonyg 8/16/2005 20:35'!skipToEndOfLine	[self peek == Character cr or: [self peek == Character lf]] whileFalse: [self next].	self skipWhitespace! !!Json methodsFor: 'private' stamp: 'tonyg 8/16/2005 20:24'!skipWhitespace	[self peek isSeparator] whileTrue: [self next].	self skipComment.! !!Json methodsFor: 'parsing' stamp: 'ms 6/27/2011 19:07'!readAny	"This is the main entry point for the JSON parser. See also readFrom: on the class side."	| c |	self skipWhitespace.	c := self peek asLowercase.	c == ${ ifTrue: [self next. ^ self readDictionary].	c == $[ ifTrue: [self next. ^ self readArray].	c == $" ifTrue: [self next. ^ self readString].	c == $t ifTrue: [^ self consume: 'true' returning: true].	c == $f ifTrue: [^ self consume: 'false' returning: false].	c == $n ifTrue: [^ self consume: 'null' returning: nil].	(Json numbersMayContain: c) ifTrue: [^ self readNumber].	"JsonSyntaxError signal: 'Unknown Json input'"! !!Json methodsFor: 'accessing' stamp: 'tonyg 8/16/2005 20:20'!stream	"Answer the value of stream"	^ stream! !!Json methodsFor: 'accessing' stamp: 'tonyg 8/16/2005 20:20'!stream: anObject	"Set the value of stream"	stream _ anObject! !!Json class methodsFor: 'as yet unclassified' stamp: 'tonyg 8/17/2005 16:20'!escapeForCharacter: c	^ CharacterEscapeMap at: c ifAbsent: [nil]! !!Json class methodsFor: 'as yet unclassified' stamp: 'tonyg 8/17/2005 16:19'!initialize	"Json initialize."	CharacterEscapeMap := Dictionary newFrom: {		$" -> $".		$\ -> $\.		Character backspace -> $b.		Character lf -> $n.		Character newPage -> $f.		Character cr -> $r.		Character tab -> $t.	}.! !!Json class methodsFor: 'as yet unclassified' stamp: 'tonyg 8/17/2005 09:09'!numbersMayContain: aChar	^ aChar isDigit or: [#($- $+ $. $e $E) includes: aChar]! !!Json class methodsFor: 'as yet unclassified' stamp: 'tonyg 8/16/2005 20:19'!readFrom: aStream	^ self new stream: aStream; readAny.! !!Json class methodsFor: 'as yet unclassified' stamp: 'tonyg 8/17/2005 07:45'!render: anObject	| s |	s := WriteStream on: String new.	anObject jsonWriteOn: s.	^ s contents.! !!Mu