eY speechArg |	selector _ command selector.	args _ command args.	selector = #mugbotFaceReset ifTrue:[		json _  self createMugbotCtrlJSON:'face_x' arg:0.		self sendJSONPacket: json.		json _  self createMugbotCtrlJSON:'face_y' arg:0.		self sendJSONPacket: json.		^ self	].	selector = #mugbotSetFaceRight: ifTrue:[		json _  self createMugbotCtrlJSON:'face_x' arg:(args at:1).	].	selector = #mugbotTurnFaceRight: ifTrue:[		turnArg _ args at:1.		faceX _   sprite faceX + turnArg.		json _  self createMugbotCtrlJSON:'face_x' arg:faceX.	].	selector = #mugbotSetFaceLeft: ifTrue:[		json _  self createMugbotCtrlJSON:'face_x' arg:(args at:1 ).	].	selector = #mugbotTurnFaceLeft: ifTrue:[		turnArg _ args at:1.		faceX _   sprite faceX + turnArg.		json _  self createMugbotCtrlJSON:'face_x' arg:faceX.	].	selector = #mugbotSetFaceUp: ifTrue:[		json _  self createMugbotCtrlJSON:'face_y' arg:(args at:1 ).	].	selector = #mugbotTurnFaceUp: ifTrue:[		turnArg _ args at:1.		faceY _   sprite faceY + turnArg.		json _  self createMugbotCtrlJSON:'face_y' arg:faceY.	].		selector = #mugbotSetFaceDown: ifTrue:[		json _  self createMugbotCtrlJSON:'face_y' arg:(args at:1 ).	].	selector = #mugbotTurnFaceDown: ifTrue:[		turnArg _ args at:1.		faceY _   (sprite faceY) + turnArg.		json _  self createMugbotCtrlJSON:'face_y' arg:faceY.	].	selector = #mugbotSpeech: ifTrue:[		speechArg _ args at:1.		json _  self createMugbotCtrlJSON:'speech' arg: speechArg.	].	selector = #mugbotSetEyesBrightness: ifTrue:[		json _  self createMugbotCtrlJSON:'eye' arg:(args at:1 ).		self sendJSONPacket: json.		^ self	].	self sendJSONPacket: json.! !!MugbotSocketAdapter methodsFor: 'as yet unclassified' stamp: 'ms 5/6/2014 00:53'!sendJSONPacket: JSON	(self isConnected = false) ifTrue: [^ self].	socket sendData: JSON, String crlf.! !!MugbotSocketAdapter methodsFor: 'as yet unclassified' stamp: 'ms 6/20/2011 19:40'!sprite: aSprite	sprite _ aSprite.! !!MugbotSocketAdapter methodsFor: 'as yet unclassified' stamp: 'ms 5/6/2014 00:45'!startCommandThread	| command |	commandThread _ [		[			commandLock ifFalse:[			(commandPool peek) ifNotNil:[				command _ commandPool next.				command forkAt: Processor userBackgroundPriority.					].		].		(Delay forMilliseconds: 10 asInteger) wait.		true] whileTrue.	] newProcess.	commandThread priority: Processor userBackgroundPriority.	commandThread resume.! !!MugbotSocketAdapter methodsFor: 'as yet unclassified' stamp: 'ms 6/18/2011 19:05'!stopCommandThread	(commandThread isKindOf: Process)		ifTrue: [ commandThread terminate.].	commandThread _ nil.! !!MugbotSocketAdapter methodsFor: 'as yet unclassified' stamp: 'ms 5/7/2014 22:14'!storeFieldsOn: anObjStream	"super storeFieldsOn: anObjStream."	self storeFieldsNamed: #(		ip port	) on: anObjStream.! !!Number methodsFor: '*JSON-writing' stamp: 'tonyg 8/17/2005 00:41'!jsonWriteOn: aWriteStream 	aWriteStream nextPutAll: self asString.! !!ObjStream class methodsFor: 'class initialization' stamp: 'ms 5/7/2014 22:29'!userClasses	"Answer an array of (<class id>, <class name>) records for all version numbered user classes."	"The following finds obsolete user classes:"	"self initialize. self userClasses reject: [:rec | Smalltalk includesKey: rec second]"	^ #(		"id		class"		(100		Morph)		(101		BorderedMorph)		(102		RectangleMorph)		(103		EllipseMorph)		(104		AlignmentMorph)		(105		StringMorph)		(106		UpdatingStringMorph)		(107		SimpleSliderMorph)		(108		SimpleButtonMorph)		(109		SampledSound)		(110		ImageMorph)		(111		SketchMorph)		"(120	SpriteMorph)"		"(121		SoundMorph)"		"(122	ImageBoxMorph)"		(123		SensorBoardMorph)		(124		ScratchSpriteMorph)		(125		ScratchStageMorph)		(140		ChoiceArgMorph)		(141		ColorArgMorph)		(142		ExpressionArgMorph)		"(143	ParameterReferenceMorph)"		"(144	PositionArgMorph)"		(145		SpriteArgMorph)		"(146	VariableArgMorph)"		(147		BlockMorph)		(148		CommandBlockMo