pe: #project.	projectName _ ''.	projectInfo _ Dictionary new.	newProject _ ScratchStageMorph new.	sprite _ ScratchFrameMorph defaultSprite fullCopy.	sprite position: (241@181) - sprite extent.	"newProject addMorph: sprite."	self installNewProject: newProject.	self initializeWatcherPositions.	justSaved _ true.	"self enterNormalMode."! !!ScratchFrameMorph methodsFor: 'private' stamp: 'ms 9/8/2014 17:41'!updateProjectName	"Update the project name display in the Scratch title bar."	| s |	projectName ifNil: [projectName _ ''].	projectTitleMorph contents: (self nameFromFileName: projectName).	projectTitleMorph contents size > 0		ifTrue: [s _ projectTitleMorph contents, '-', AppName]		ifFalse: [s _ AppName, ' ', Version].	ScratchPlugin primSetWindowTitle: s.	self fixLayout.! !!ScratchFrameMorph class methodsFor: 'class initialization' stamp: 'ms 6/20/2011 02:53'!initialize	"self initialize"	Clipboard _ nil.	WorkpaneExtent _ 480@360.	UseErrorCatcher _ false.	DefaultNotes _ ''.	self initFonts.! !!ScratchFrameMorph class methodsFor: 'utilities' stamp: 'ms 9/8/2014 17:39'!appName	^ AppName! !!ScratchFrameMorph class methodsFor: 'utilities' stamp: 'ms 9/8/2014 17:40'!appName: aString	AppName _ aString.! !!ScratchFrameMorph class methodsFor: 'utilities' stamp: 'ms 9/8/2014 17:24'!build	^ Build! !!ScratchFrameMorph class methodsFor: 'utilities' stamp: 'ms 9/8/2014 17:25'!build: aString	Build _ aString.! !!ScratchFrameMorph class methodsFor: 'utilities' stamp: 'ms 9/8/2014 17:32'!initializeMugbotLogo	ScratchSkin at: #scratchLogo put: (Form fromFileNamed: 'logo.png').! !!ScratchFrameMorph class methodsFor: 'utilities' stamp: 'ms 9/8/2014 17:25'!version: aString	Version _ aString.	VersionDate _ Date today printFormat: #(3 2 1 $- 1 1).! !!ScratchProcess methodsFor: 'private-evaluation' stamp: 'ms 5/4/2015 22:19'!applyTimedCommand	"Applies the current command to the already evaluated list of arguments over a particular time interval."	| block arguments currentTime startTime args totalMSecs elapsedMSecs |	block _ stackFrame expression.	arguments _ stackFrame arguments.		"Do we still need to evaluate more arguments?"	arguments size < block argumentCount ifTrue: [^ self evaluateNextArgument].	arguments _ block coerceArgs: arguments.	"Record or get the time when command was first invoked."	currentTime _ Time millisecondClockValue.	startTime _ stackFrame startTime.	startTime ifNil: [  "first call; just set starting time and value"		((((((((((block selector = #mugbotSetFaceRight:) 		or: [block selector = #mugbotSetFaceLeft:])		or: [block selector = #mugbotTurnFaceRight:])		or: [block selector = #mugbotTurnFaceLeft:]) 		or: [block selector = #mugbotSetFaceUp:]) 		or: [block selector = #mugbotTurnFaceUp:])		or: [block selector = #mugbotSetFaceDown:]) 		or: [block selector = #mugbotTurnFaceDown:])		or: [block selector = #mugbotSetEyesBrightness:])		or: [block selector = #mugbotSpeech:]) ifTrue: [			args _ arguments asArray.		] ifFalse:[			args _ arguments asArray, (Array with: 0 with: nil).		].		stackFrame startValue: (block receiver perform: block selector withArguments: args).		stackFrame startTime: currentTime.		readyToYield _ true.		^ self].	"Call primitive time command with its arguments and the elapsed time in seconds"	arguments last isNumber ifTrue:[		totalMSecs _ arguments last * 1000.	].	block selector = #glideSecs:toX:y:elapsed:from: ifTrue: [totalMSecs _ arguments first * 1000].	block selector = #mwait:elapsed:from: ifTrue: [totalMSecs _ arguments last].	((block selector = #drum:duration:elapsed:from:) or:	 [block selector = #noteOn:duration:elapsed:from:])		ifTrue: [totalMSecs _ (60000 * arguments second) / block receiver tempo].	block selector = #rest:elapsed:from:		ifTrue: [totalMSecs _ (60000 * arguments first) / block receiver tempo].	((((((((((block selector = #mugbotSetFaceRight:) 		or: [block selector = #mugbotSetFaceLeft:])		or: [block selector = #mugbotTurnFaceRight:])		or: [block selector = #mugbotTurnFaceLeft:]) 		or: [block selector = #mugbotSetFaceUp:])		or: [block selector = #mugbotTurnFaceUp:]) 		or: [block selector = #mugbotSetFaceDown:])		or: [block selector = #mugbotTurnFaceDown:]) 		or: [block selector = #mugbotSetEyesBrightness:])		or: [block selector = #mugbotSpeech:]) ifTrue: [		totalMSecs _ block receiver getTotalMSecs: (block selector) args: arguments. 	].	block selector = #rest:elapsed:from:		ifTrue: [totalMSecs _ (60000 * arguments first) / block receiver tempo].	elapsedMSecs _ currentTime - startTime.	currentTime < startTime ifTrue: [		elapsedMSecs _ totalMSecs.	].  "clock wrap"	"If not done, then we leave stack as is and yield."	elapsedMSecs < totalMSecs ifTrue: [		readyToYield _ true.		^ self].	"Pop this command off the stack and return."	self popStackFrame.	block receiver class = MugbotSpriteMorph ifTrue:[		block receiver updateFacePosition: (block selector) args: arguments. 	].! !!ScriptableScratchMorph methodsFor: 'blocks' stamp: 'ms 5/7/2014 23:02'!defaultArgsFor: blockSpec	"Answer the default argument for the given block specification."	| defaultArgs stage sel currentSize list |	defaultArgs _ blockSpec copyFrom: 4 to: blockSpec size.  "may be empty"	stage _ self ownerThatIsA: ScratchStageMorph.	sel _ (blockSpec at: 3) asSymbol.	#gotoX:y: = sel ifTrue: [		defaultArgs _ Array			with: self referencePosition x rounded			with: self referencePosition y rounded].	#glideSecs:toX:y:elapsed:from: = sel ifTrue: [		defaultArgs _ Array			with: 1			with: self referencePosition x rounded			with: self referencePosition y rounded].	#motor:direction: = self ifTrue: [		defaultArgs _ Array with: 'reverse' localized with: 'this way' localized with: 'that way'].	#setSizeTo: = sel ifTrue: [		currentSize _ (100.0 * (self scalePoint x max: self scalePoint y)) rounded.		defaultArgs _ Array with: currentSize].	#getAttribute:of: = sel ifTrue: [		(stage _ self ownerThatIsA: ScratchStageMorph) ifNotNil: [			list _ stage submorphs select: [:m | m isKindOf: ScratchSpriteMorph].			list sort: [:s1 :s2 | s1 objName asLowercase < s2 objName asLowercase].			list size > 0				ifTrue: [defaultArgs _ Array with: 'x position' with: list first]				ifFalse: [defaultArgs _ Array with: 'background #' with: stage]]		ifNil:[defaultArgs _ Array with: 'x position' with: self]].	#concatenate:with: = sel ifTrue: [		defaultArgs _ Array with: 'hello ' localized with: 'world' localized].	#doAsk = sel ifTrue: [		defaultArgs _ Array with: 'What''s your name?' localized].	#letter:of: = sel ifTrue: [		defaultArgs _ Array with: 1 with: 'world' localized].	#stringLength: = sel ifTrue: [		defaultArgs _ Array with: 'world' localized].	#say:duration:elapsed:from: = sel ifTrue: [		defaultArgs _ Array with: 'Hello!!' localized with: 2].	#say: = sel ifTrue: [		defaultArgs _ Array with: 'Hello!!' localized].	#think:duration:elapsed:from: = sel ifTrue: [		defaultArgs _ Array with: 'Hmm...' localized with: 2].	#think: = sel ifTrue: [		defaultArgs _ Array with: 'Hmm...' localized].	(#(lookLike: showBackground:) includes: sel) ifTrue: [		defaultArgs _ Array with: self costumeNames last].	(#(playSound: doPlaySoundAndWait) includes: sel) ifTrue: [		list _ self soundNames.		defaultArgs _ list size <= 2			ifTrue: [Array with: '']			ifFalse: [Array with: (list at: (list size - 2))]].	(#(broadcast: doBroadcastAndWait) includes: sel) ifTrue: [		stage ifNotNil: [defaultArgs _ Array with: stage defaultEventName]].	(#(append:toList: deleteLine:ofList: insert:at:ofList:) includes: sel) ifTrue: [		defaultArgs size >= 1 ifTrue: [			defaultArgs at: 1 put: (defaultArgs at: 1) localized]].	(#(append:toList: deleteLine:ofList: getLine:ofList: insert:at:ofList: lineCountOfList:)		includes: sel) ifTrue: [			defaultArgs _ defaultArgs copyWith: self defaultListName].	#setLine:ofList:to: = sel ifTrue: [		defaultArgs size >= 3 ifTrue: [			defaultArgs at: 2 put: self defaultListName.			defaultArgs at: 3 put: (defaultArgs at: 3) localized]].	#appendLettersOf:toList: = sel ifTrue: [		defaultArgs size >= 2 ifTrue: [			defaultArgs at: 1 put: (defaultArgs at: 1) localized.			defaultArgs at: 2 put: self defaultListName]].	#list:contains: = sel ifTrue: [		defaultArgs size >= 2 ifTrue: [			defaultArgs at: 1 put: self defaultListName.			defaultArgs at: 2 put: (defaultArgs at: 2) localized]].	#mugbotSpeech: = sel ifTrue: [		defaultArgs _ Array with: 'I''m Mugbot!!' localized].	^ defaultArgs! !!ScratchSpriteMorph methodsFor: 'sensing ops' stamp: 'ms 1/4/2016 14:14'!getDateValue: type 	| timeNow dateNow |	timeNow _ Time now.	dateNow _ Date today.	'time' = type ifTrue: [^ timeNow asString].	'hour' = type ifTrue: [^ timeNow hours].	'minute' = type ifTrue: [^ timeNow minutes].	'second' = type ifTrue: [^ timeNow seconds].	'date' = type ifTrue: [^ dateNow asString].	'weekday-name' = type ifTrue: [^ dateNow weekday localized].	'weekday-number' = type		ifTrue: 			[dateNow weekday = 'Monday' ifTrue: [^ 1].			dateNow weekday = 'Tuesday' ifTrue: [^ 2].			dateNow weekday = 'Wednesday' ifTrue: [^ 3].			dateNow weekday = 'Thursday' ifTrue: [^ 4].			dateNow weekday = 'Friday' ifTrue: [^ 5].			dateNow weekday = 'Saturday' ifTrue: [^ 6].			dateNow weekday = 'Sunday' ifTrue: [^ 7]].	'month-japanese-name' = type ifTrue: [^ dateNow monthName asString localized].	'month-name' = type ifTrue: [^ dateNow monthName asString].	'month-number' = type ifTrue: [^ dateNow monthIndex].	'day of month' = type ifTrue: [^ dateNow dayOfMonth].	'day of year' = type ifTrue: [^ dateNow daysInYear - dateNow daysLeftInYear].	'year' = type ifTrue: [^ dateNow year].	'julian day number' = type ifTrue: [^ dateNow julianDayNumber].	^ 0! !!ScratchSpriteMorph methodsFor: 'sensing ops' stamp: 'ms 1/4/2016 14:13'!getDates	^ #('time' 'hour' 'minute' 'second' 'date' 'day of month' 'day of year' 'weekday-name' 'weekday-number' 'month-number' 'month-name' 'month-japanese-name' 'year' 'julian day number')! !!MugbotSpriteMorph methodsFor: 'initialization' stamp: 'ms 5/1/2014 16:40'!delete	super delete.	adapter stopCommandThread.	adapter closeSocket.	(motionThread isKindOf: Process)		ifTrue: [ motionThread terminate.].	motionThread _ nil.	! !!MugbotSpriteMorph methodsFor: 'initialization' stamp: 'ms 5/4/2015 22:49'!initialize	super initialize.	faceX _ 0.	faceY _ 0.	self importMedia: (self costumePath: 'mugbot.png').	adapter isNil ifTrue:[		adapter _ MugbotSocketAdapter new.		adapter isConnected ifFalse:[			^super.		].		adapter sprite: self.	].	motionPool _ SharedQueue new.	motionLock _ false.		self startMotionThread.	self mugbotFaceReset.! !!MugbotSpriteMorph methodsFor: 'initialization' stamp: 'ms 5/7/2014 22:37'!updateFacePosition: selector args: args	| turnArg |	(selector = #mugbotSetFaceRight:) ifTrue: [		((args first) < 0) ifTrue:[			args first < -85 ifTrue:[				faceX _ 85.			] ifFalse:[				faceX _ -1 * args first.			].		] ifFalse:[			args first > 85 ifTrue:[				faceX _ -85.			] ifFalse:[				faceX _ -1 * args first.			].			].	].	(selector = #mugbotTurnFaceRight:) ifTrue: [		turnArg _ -1 * (args at:1).		(turnArg > 0) ifTrue:[			faceX + turnArg > 85 ifTrue:[				faceX _ 85.			] ifFalse:[				faceX _ faceX + turnArg.			].		] ifFalse:[			 faceX + turnArg < -85 ifTrue:[				faceX _ -85.			] ifFalse:[				faceX _ faceX + turnArg.			].			].	].	(selector = #mugbotSetFaceLeft:) ifTrue: [		((args first) < 0) ifTrue:[			args first < -85 ifTrue:[				faceX _ -85.			] ifFalse:[				faceX _ args first.			].		] ifFalse:[			args first > 85 ifTrue:[				faceX _ 85.			] ifFalse:[				faceX _ args first.			].			].	].	(selector = #mugbotTurnFaceLeft:) ifTrue: [		turnArg _ args at:1.		(turnArg > 0) ifTrue:[			faceX + turnArg > 85 ifTrue:[				faceX _ 85.			] ifFalse:[				faceX _ faceX + turnArg.			].		] ifFalse:[			 faceX + turnArg < -85 ifTrue:[				faceX _ -85.			] ifFalse:[				faceX _ faceX + turnArg.			].			].	].	(selector = #mugbotSetFaceUp:) ifTrue: [		((args first) < 0) ifTrue:[			args first < -15 ifTrue:[				faceY _ -15.			] ifFalse:[				faceY _ args first.			].		] ifFalse:[			args first > 15 ifTrue:[				faceY _ 15.			] ifFalse:[				faceY _ args first.			].			].	].	(selector = #mugbotTurnFaceUp:) ifTrue: [		(args first > 0) ifTrue:[			faceY + args first > 15 ifTrue:[				faceY _ 15.			] ifFalse:[				faceY _ faceY + args first.			].		] ifFalse:[			 faceY +  args first < -15 ifTrue:[				faceY _ -15.			] ifFalse:[				faceY _ faceY + args first.			].			].	].	(selector = #mugbotSetFaceDown:) ifTrue: [		((args first) < 0) ifTrue:[			args first < -15 ifTrue:[				faceY _ 15.			] ifFalse:[				faceY _ -1 * args first.			].		] ifFalse:[			args first > 15 ifTrue:[				faceY _ -15.			] ifFalse:[				faceY _ -1 * args first.			].			].	].	(selector = #mugbotTurnFaceDown:) ifTrue: [		(args first > 0) ifTrue:[			faceY - args first < -15 ifTrue:[				faceY _ -15.			] ifFalse:[				faceY _ faceY - args first.			].		] ifFalse:[			 faceY -  args first > 15 ifTrue:[				faceY _ 15.			] ifFalse:[				faceY _ faceY - args first.			].			].	].	(selector = #mugbotSpeech:) ifTrue: [		self say: ''	].! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 5/1/2014 16:39'!defaultImageMedia	| defaultImageMedia |	defaultImageMedia _ super defaultImageMedia.	defaultImageMedia mediaName: ('Mugbot' localized, '1').	^ defaultImageMedia.! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 5/6/2014 12:07'!faceX	^ faceX! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 5/6/2014 07:20'!faceX: x	faceX _ x.! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 5/6/2014 12:08'!faceY	^ faceY! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 5/6/2014 07:20'!faceY: y	faceY _ y.! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 5/4/2015 22:33'!getTotalMSecs: selector args: args	| secs |	(selector = #mugbotSetFaceRight:) ifTrue: [		secs _ (faceX + args first) abs * 5.		^ secs.	].	(selector = #mugbotTurnFaceRight:) ifTrue: [		secs _ (args first * 5) abs.		^ secs.	].	(selector = #mugbotSetFaceLeft:) ifTrue: [		secs _ (args first - faceX) abs * 5.		^ secs.	].	(selector = #mugbotTurnFaceLeft:) ifTrue: [		secs _ (args first * 5) abs.		^ secs.	].	(selector = #mugbotSetFaceUp:) ifTrue: [		secs _ (args first - faceY) abs * 10.		^ secs.	].	(selector = #mugbotTurnFaceUp:) ifTrue: [		secs _ (args first * 5) abs.		^ secs.	].	(selector = #mugbotSetFaceDown:) ifTrue: [		secs _ (faceY + args first) abs * 10.		^ secs.	].	(selector = #mugbotTurnFaceDown:) ifTrue: [		secs _ (args first * 5) abs.		^ secs.	].	(selector = #mugbotSpeech:) ifTrue: [		secs _ args first asString size * 90 + 1000.		^ secs.	].	(selector = #mugbotSetEyesBrightness:) ifTrue: [		secs _ 5.		^ secs.	].! !!MugbotSpriteMorph methodsFor: 'accessing' stamp: 'ms 6/20/2011 21:50'!lock	^ lock! !!MugbotSpriteMorph methodsFor: 'motion ops' stamp: 'ms 5/6/2014 07:08'!mugbotFaceReset	| command |		command _ MugbotCommand new. 	command selector: #mugbotFaceReset.	adapter send: command.! !!MugbotSpriteMorph methodsFor: 'motion ops' stamp: 'ms 5/7/2014 13:47'!mugbotSetFaceDown: deg	| args command |		command _ MugbotCommand new. 	command selector: #mugbotSetFaceDown:.	args _ Array with: (-1 * deg).	command args: args.	adapter send: command.! !!MugbotSpriteMorph methodsFor: 'motion ops' stamp: 'ms 5/6/2014 08:29'!mugbotSetFaceLeft: deg	| args command |		command _ MugbotCommand new. 	command selector: #mugbotSetFaceLeft:.	args _ Array with: deg.	command args: args.	adapter send: command.! !!MugbotSpriteMorph methodsFor: 'motion ops' stamp: 'ms 5/6/2014 16:47'!mugbotSetFaceRight: deg	